        public async void Load0(string id, int type)
        {
            HttpWebRequest request = (HttpWebRequest)WebRequest.Create("https://readerservercore.azurewebsites.net/api/get/book?name=" + id + type.ToString());
            HttpWebResponse response = (HttpWebResponse)await request.GetResponseAsync();
            using (Stream stream = response.GetResponseStream())
            {
                using (StreamReader reader = new StreamReader(stream))
                {
                    SL.Text = await reader.ReadToEndAsync();
                    SL.TextType = TextType.Html;
                }
            }
            response.Close();
            loading.IsVisible = false;
        }
        List<string> listToMode3 = new List<string>();
        int numOnMode3 = 0;
        bool canNext = true;
        public async void Load3(string id)
        {
            mode3Grid.IsVisible = true;
            mode3Stop.IsVisible = true;
            var swipe = new SwipeGestureRecognizer();
            swipe.Swiped += (sender, args) =>
            {
                if (args.Direction == SwipeDirection.Down)
                    numOnMode3--;
                if (args.Direction == SwipeDirection.Up)
                    numOnMode3++;
                try
                {
                    UpText.Text = listToMode3[numOnMode3 - 1];
                }
                catch { }
                try
                {
                    DownText.Text = listToMode3[numOnMode3 + 1];
                }
                catch { }
                CenterText.Text = listToMode3[numOnMode3];
            };
            var tgr = new TapGestureRecognizer { NumberOfTapsRequired = 1 };
            tgr.Tapped += (sender, args) =>
            {
                canNext = !canNext;
            };
            mode3Stop.GestureRecognizers.Add(tgr);
            mode3Stop.GestureRecognizers.Add(swipe);
            HttpWebRequest request = (HttpWebRequest)WebRequest.Create("https://readerservercore.azurewebsites.net/api/get/book?name=" + id + "2");
            HttpWebResponse response = (HttpWebResponse)await request.GetResponseAsync();
            using (Stream stream = response.GetResponseStream())
            {
                using (StreamReader reader = new StreamReader(stream))
                {
                    for (int i = 0; i < 5; i++)
                        listToMode3.Add(i.ToString());
                    foreach (var s in reader.ReadToEnd().Split(new string[] { " ", "\n" }, StringSplitOptions.RemoveEmptyEntries).Select(mobile => mobile.Trim()).Where(s => s != string.Empty).ToList())
                        listToMode3.Add(s);
                }
            }
            response.Close();
            loading.IsVisible = false;
            Device.StartTimer(new TimeSpan(0, 0, 0, 0, 500), () =>
            {
                if (numOnMode3 >= listToMode3.Count)
                    return false;

                try
                {
                    UpText.Text = listToMode3[numOnMode3 - 1];
                }
                catch { }
                try
                {
                    DownText.Text = listToMode3[numOnMode3 + 1];
                }
                catch { }
                CenterText.Text = listToMode3[numOnMode3];
                if (canNext)
                    numOnMode3++;
                return true;
            });
        }

