        private void OnUpSwiped(object sender, SwipedEventArgs e)
        {
            numOnMode3++;
            try
            {
                UpText.Text = listToMode3[numOnMode3 - 1];
            }
            catch { }
            try
            {
                DownText.Text = listToMode3[numOnMode3 + 1];
            }
            catch { }
            CenterText.Text = listToMode3[numOnMode3];
        }
        private void OnDownSwiped(object sender, SwipedEventArgs e)
        {
            numOnMode3--;
            try
            {
                UpText.Text = listToMode3[numOnMode3 - 1];
            }
            catch { }
            try
            {
                DownText.Text = listToMode3[numOnMode3 + 1];
            }
            catch { }
            CenterText.Text = listToMode3[numOnMode3];
        }

        private void StartHold(object sender, DragStartingEventArgs e)
        {
            canNext = false;
        }

